CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

CREATE TABLE "Abouts" (
    "AboutID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Title" text NULL,
    "Description" text NULL,
    "Age" text NULL,
    "Mail" text NULL,
    "Phone" text NULL,
    "Address" text NULL,
    "ImageUrl" text NULL,
    CONSTRAINT "PK_Abouts" PRIMARY KEY ("AboutID")
);

CREATE TABLE "Contacts" (
    "ContactID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Title" text NULL,
    "Description" text NULL,
    "PhoneNumber" text NULL,
    CONSTRAINT "PK_Contacts" PRIMARY KEY ("ContactID")
);

CREATE TABLE "Experiences" (
    "ExperienceID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "Date" text NULL,
    "ImageUrl" text NULL,
    "Description" text NULL,
    CONSTRAINT "PK_Experiences" PRIMARY KEY ("ExperienceID")
);

CREATE TABLE "Features" (
    "FeatureID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Header" text NULL,
    "Name" text NULL,
    "Title" text NULL,
    CONSTRAINT "PK_Features" PRIMARY KEY ("FeatureID")
);

CREATE TABLE "Messages" (
    "MessageID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "Mail" text NULL,
    "Content" text NULL,
    "Date" timestamp without time zone NOT NULL,
    "Status" boolean NOT NULL,
    CONSTRAINT "PK_Messages" PRIMARY KEY ("MessageID")
);

CREATE TABLE "Portfolios" (
    "PortfolioID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "ImageUrl" text NULL,
    CONSTRAINT "PK_Portfolios" PRIMARY KEY ("PortfolioID")
);

CREATE TABLE "Services" (
    "ServiceID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Title" text NULL,
    "ImageUrl" text NULL,
    CONSTRAINT "PK_Services" PRIMARY KEY ("ServiceID")
);

CREATE TABLE "Skills" (
    "SkillID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Title" text NULL,
    "Value" text NULL,
    CONSTRAINT "PK_Skills" PRIMARY KEY ("SkillID")
);

CREATE TABLE "SocialMedias" (
    "SocialMediaID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "Url" text NULL,
    "Icon" text NULL,
    "Status" boolean NOT NULL,
    CONSTRAINT "PK_SocialMedias" PRIMARY KEY ("SocialMediaID")
);

CREATE TABLE "Testimonials" (
    "TestimonialID" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "ClientName" text NULL,
    "Company" text NULL,
    "Comment" text NULL,
    "Image" text NULL,
    CONSTRAINT "PK_Testimonials" PRIMARY KEY ("TestimonialID")
);

